import React from "react";
import classes from "./Messages.module.css"
import Dialogs from "./Dialogs/Dialogs";
import MessageItems from "./MessagesItems/MessageItems";

const Messages = (props) => {
    debugger

    let dialogsElements = props.state.dialogs.map(user => <Dialogs name={user.name} id={user.id}/>);
    let messagesElements = props.state.messages.map(message => <MessageItems text={message.text}/>);

    let text = React.createRef();

    let addMessage = () => {
        props.addMessages()
    };

    let messageOnChange = () => {
        let message = text.current.value;
        props.changeNewMessageText(message)
    };

    return (
        <div className={classes.dialogs}>
            <div className={classes.dialogs_item}>
                {dialogsElements}
            </div>

            <div className={classes.messages}>
                {messagesElements}
                <div>
                    <textarea onChange={messageOnChange} ref={text} value={props.newMessageText}/>
                    <div>
                        <button onClick={addMessage}>Send message</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Messages
